import { useState } from 'react'
import './Login.css'
import { toast } from 'react-toastify'
import { createUserWithEmailAndPassword, signInWithEmailAndPassword } from 'firebase/auth'
import { auth, db } from '../../lib/firebase'
import { doc, setDoc } from "firebase/firestore"; 
import upload from '../../lib/upload'
import {api_base_url}  from '../../Helper'
import { useNavigate } from 'react-router-dom'

export default function Login() {
  const navigate = useNavigate()
  const [loading, setLoading] = useState(false)
  const [avatar, setAvatar] = useState({
    file:null,
    url:""
  })

  const handleAvatar = e =>{
    if(e.target.files[0]){
      setAvatar({
        file: e.target.files[0],
        url: URL.createObjectURL(e.target.files[0])
      })
    }
  }

  const createUser = ({username, email, UID, avatar})=>{
    fetch(api_base_url + "/singUp", {
        mode:"cors",
        method: "POST",
        headers:{
            "Content-Type":"application/json",
        },
        body: JSON.stringify({
            username:username,
            email:email,
            userId:UID,
            avatar:avatar
        }),
    })
    .then((res)=> res.json())
    .then((data)=>{
        if(data.seccess === false){
            toast.error(data.message)
          }else{
          toast.success("Conta Criada Com Sucesso!")
        }
    })
}

  const handleRegister = async ( e ) =>{
    e.preventDefault()
    setLoading(true)

    const formData = new FormData(e.target)

    const { username, email, password} = Object.fromEntries(formData)

    try {
      const res = await createUserWithEmailAndPassword(auth, email, password)

      const imgUrl = await upload(avatar.file)
      const UID = res.user.uid


      await setDoc(doc(db, "users", res.user.uid), {
        username,
        email,
        avatar:imgUrl,
        id:res.user.uid,
        blocked: [],
        grups: [],
      });


      await setDoc(doc(db, "userchats", res.user.uid), {
        chats: [],
      });

      toast.success("Conta criada com Sucesso!")
      createUser({ username, email, UID, imgUrl})

    } catch (error) {
      toast.error(error.message)
    } finally{
      setLoading(false)
    }
  }

  const hendleLogin = async ( e ) =>{
    e.preventDefault()
    setLoading(true)
    const formData = new FormData(e.target)
    const { email, password} = Object.fromEntries(formData)

    try {
      await signInWithEmailAndPassword(auth, email, password)
      toast.success("Logado com sucesso")
      navigate("/feed")
    } catch (error) {
      toast.error(error.message)
      setLoading(false)
    } finally{
      setLoading(false)
    }
  }


  return (
    <div className='container'>
      <div className="login">
        <div className="item">
          <h2>Seja Bem-vindo.</h2>
          <form onSubmit={hendleLogin}>
            <input type="enail" placeholder='Email' name='email' />
            <input type="password" placeholder='Senha' name='password' />
            <button disabled={loading}>{ loading ? "Loading..." : "Entrar"}</button>
          </form>
        </div>
        <div className="separator"></div>
        <div className="item">
          <h2>Criar conta.</h2>
            <form onSubmit={handleRegister}>
              <label htmlFor="file">
                <img src={avatar.url || "data:image/png;base64,"} alt="" />
                Adiciona foto do perfil</label>
              <input type="file" id='file' style={{display: "none"}} onChange={handleAvatar} />
              <input type="text" placeholder='UsuÃ¡rio' name='username' />
              <input type="email" placeholder='Email' name='email' />
              <input type="password" placeholder='Senha' name='password' />
              <button disabled={loading}>{ loading ? "Loading..." : "Criar Conta"}</button>
            </form>
        </div>
      </div>
    </div>
  )
}
